css:
.diagnostic-report {
    margin-top: 0px;
    padding-top: 0px;
    margin-right:15px;
	margin-left:15px;
}
.observation {
    page-break-inside: avoid;
	margin-bottom: 10px;
	border: 1px solid #dadce0;
	margin-right:15px;
	margin-left:15px;
	border-radius: 5px !important;
}

.grouped-obs {
	border: 1px solid #dadce0;
	font-size: 11px;
	padding-top: 5px;
	border-radius: 10px;
	margin-bottom: 5px;
	margin-left: 10px;
	margin-right: 10px;
}

.observation-details {
    page-break-inside: avoid;
	padding: 10px;
	display: -webkit-box;
	display: flex;
}

.note {
	padding-right: 15px;
	margin-left: 15px;
	text-align: left;
	padding-bottom: 3px;
}

.obs-first-line {
    font-size: 13px;
}

.ref-range {
    font-size: 12px !important;
}
.obs-second-line {
    font-size: 10px;
}

.obs-sh-first-line {
    font-size: 11px;
}

